const getPage = async (req, res, next) => {
  const promise = new Promise(async (resolve, reject) => {
    const {
      localeId,
      username
    } = req.query;
    const {
      user
    } = req;

    const page = _url.default.parse(req.url).pathname;

    let data = null;

    try {
      switch (page) {
        case '/login':
          {
            data = await getLoginPageData(localeId);
            break;
          }

        case '/register':
          {
            data = await getRegistrationPageData(localeId);
            break;
          }

        case '/view':
          {
            if (username) {
              data = await getViewPageData(localeId, username);
              break;
            }
          }

        case '/edit':
          {
            if (user.id) {
              data = await getEditPageData(localeId, user.id);
              break;
            }
          }

        default:
          {
            break;
          }
      }

      if (!data) {
        reject('No data found');
      }

      resolve(data);
    } catch (err) {
      console.log(err);
      reject(err);
    }
  });
  promise.then(data => res.status(200).send(data)).catch(err => {
    console.log(err);
    res.status(403).send(err);
  });
};

